import React from "react";
import {validate} from './validation'
import { Login } from "../../styles/styled-components";

export default function Form({login}){
    const [userData, setUserData] = React.useState({
        username: "",
        password: ""
    });

    const [errors, setErrors] = React.useState({}); 

    function handleInputChange(event){
        setErrors(validate({
            ...userData,
            [event.target.name]: event.target.value
        }))
        setUserData({
            ...userData,
            [event.target.name]: event.target.value
        })
    }

    function handleSubmit(event){
        event.preventDefault();
        login(userData);
    }

    return (<div>
        <form onSubmit={handleSubmit}>
            <label htmlFor="username">Username:</label>
            <input type="text" name="username" onChange={handleInputChange} value={userData.username} placeholder="trechi@mail.com"/>
            <p>{errors.username}</p>

            <label htmlFor="password">Password:</label>
            <input type="password" name="password" onChange={handleInputChange} value={userData.password} placeholder="trechi123"/>
            <p>{errors.password}</p>

            <button type="submit">LOGIN</button>
        </form>
    </div>)
}
