import { useParams } from "react-router-dom"
import { useEffect, useState } from "react";
import { Detalles, DatosDetail, Imagen } from "../../styles/styled-components";

export default function Detail(props){

    const [character, setCharacter] = useState({});
    const {detailId} = useParams();

    useEffect(() => {
        fetch(`https://rickandmortyapi.com/api/character/${detailId}`)
          .then((response) => response.json())
          .then((char) => {
            if (char.name) {
              setCharacter(char);
            } else {
              window.alert("No hay personajes con ese ID");
            }
          })
          .catch((err) => {
            window.alert("No hay personajes con ese ID");
          });
        return setCharacter({});
      }, [detailId]);

    return(
        <div>
            {
                    character.id ? (
                    <div style={{display: "flex"}}>
                    <Detalles>
                        <DatosDetail>Name: {character.name}</DatosDetail>
                        <DatosDetail>Status: {character.status}</DatosDetail>
                        <DatosDetail>Specie: {character.species}</DatosDetail>
                        <DatosDetail>Gender: {character.gender}</DatosDetail>
                        {/* <Datos>Origen: {character.origin?.name}</Datos> */}
                    </Detalles>
                    <Imagen src={character.image} alt={character.name} ></Imagen>
                </div>) : <DatosDetail>Loading...</DatosDetail>
            }
        </div>
    )
}
