import {CloseBtn, ContCartas} from './styled-components'
export default function Card({name, species, gender, image, onClose}) {
   
   function onCerrar(){
      return window.alert('Emulamos que se cierra la card')
   }

   return (
      <div>
         <CloseBtn onClick={onCerrar()}>X</CloseBtn>
         <h2>{name}</h2>
         <h2>{species}</h2>
         <h2>{gender}</h2>
         <ContCartas>
         <img src={image} alt={name} />
         </ContCartas>
         <hr />
      </div>
     
   );
}
