import './App.css';
import {useState} from 'react'
import Cards from './components/Card/Cards.jsx';
// import characters from './data.js';
import {Container, ContCartas} from './styles/styled-components.jsx';
import Nav from './components/Nav/Nav';
import {Routes, Route} from 'react-router-dom';
import About from './components/About/About'
import Detail from './components/Detail/Detail'

function App () {

  const [characters, setCharacters] = useState([]);
  function onSearch(character) {
    fetch (`https://rickandmortyapi.com/api/character/${character}`)
       .then((response) => response.json())
       .then((data) => {
          if (data.name) {
             const alreadyExists = characters.some(char => char.id === data.id);
             if (!alreadyExists) {
                setCharacters((oldChars) => [...oldChars, data]);
             } else {
                window.alert(`El personaje ${data.name} ya ha sido agregado.`);
             }
          } else {
             window.alert('No hay personajes con ese ID');
          }
       });
 }

  function onClose(id){
    setCharacters(characters.filter(personaje => personaje.id !== id))
  }

  return (
    <Container>
    <div className='App'>
         
        <Nav onSearch={onSearch}/>

        <Routes>

         
          <Route 
          path = "/home" 
          element={<ContCartas><Cards characters={characters} onClose={onClose}/></ContCartas>}/>
          <Route path = "/about" element={<About />}/>
          <Route path = "/detail/:detailId" element={<Detail />}/>
        </Routes>
    </div>
    </Container>
  )
}

export default App
