import { useParams } from "react-router-dom"
import { useEffect, useState } from "react";
import { Data, Datos, Imagen } from "../../styles/styled-components";

export default function Detail(props){

    const [character, setCharacter] = useState({});
    const {detailId} = useParams();

    useEffect(() => {
        fetch(`https://rickandmortyapi.com/api/character/${detailId}`)
          .then((response) => response.json())
          .then((char) => {
            if (char.name) {
              setCharacter(char);
            } else {
              window.alert("No hay personajes con ese ID");
            }
          })
          .catch((err) => {
            window.alert("No hay personajes con ese ID");
          });
        return setCharacter({});
      }, [detailId]);

    return(
        <div>
            {
                    character.id ? <Data>
                    <Datos>Name: {character.name}</Datos>
                    <Datos>Status: {character.status}</Datos>
                    <Datos>{character.species}</Datos>
                    <Datos>{character.gender}</Datos>
                    <Datos>{character.origin?.name}</Datos>

                    <Imagen src={character.image} alt={character.name}></Imagen>
                </Data> : <Datos>Loading...</Datos>
            }
        </div>
    )
}
