import styled from 'styled-components'
import img from './background.jpg'

const Container = styled.div`
    max-width: 100hv
    min-height: 100vh;
    background-image: url(${img});
    background-size: cover;
    background-color: black;
`;

const ContCartas = styled.div`
    display: flex;
    justify-content: space-around;
    gap: 30%;
`
const Carta = styled.div`
    background-color: black;
    display: inline-block;
    border: 1px solid black;
    border-radius: 2%;    
    &:hover{
        box-shadow: 0 0 80px #FFFFFF;
        background-color: white;
}
    
`
const Imagen = styled.img`
    border-radius: 4%;
    
`

const CloseBtn = styled.button`
    border-color: white;
    color: white;
    background-color: red;
    border-radius: 50%;
    &:hover{
        border-color: red;
        background-color: white;
        color: red;
}
`;

const Data = styled.div`
    display: flex;
    justify-content: space-around;
    background-color: rgb(14, 82, 64);
    color: rgb(158, 230, 210);
    padding-left 15%;
    padding-right: 15%;
    border-radius: 6%;
    border: 1px solid white;
    
    
`
const ContBtn = styled.div`
    display: flex;
    justify-content: flex-end;    
    background-color: rgb(6, 52, 40);
    color: rgb(158, 230, 210);
    border-radius: 6%;
    border: 1px solid white;
    border-bottom: none;
`
const Datos = styled.h2`
    padding: 2px;
    background-color: rgb(255, 255, 255, 0.8); 
    text-align: center;
    color: rgb(14, 82, 64);
    box-shadow: 0px 0px 25px rgba(255, 255, 0, 0.5);
    border-radius: 4px;
`
export {
    CloseBtn,
    Container,
    ContCartas,
    Carta,
    Imagen,
    Data,
    ContBtn,
    Datos
}
