import { useParams } from "react-router-dom"
import { useEffect, useState } from "react";
import { Detalles, Datos, Imagen } from "../../styles/styled-components";

export default function Detail(props){

    const [character, setCharacter] = useState({});
    const {detailId} = useParams();

    useEffect(() => {
        fetch(`https://rickandmortyapi.com/api/character/${detailId}`)
          .then((response) => response.json())
          .then((char) => {
            if (char.name) {
              setCharacter(char);
            } else {
              window.alert("No hay personajes con ese ID");
            }
          })
          .catch((err) => {
            window.alert("No hay personajes con ese ID");
          });
        return setCharacter({});
      }, [detailId]);

    return(
        <div>
            {
                    character.id ? (
                    <div style={{alignItems}}>
                    <Detalles>
                        <Datos>Name: {character.name}</Datos>
                        <Datos>Status: {character.status}</Datos>
                        <Datos>Specie: {character.species}</Datos>
                        <Datos>Gender: {character.gender}</Datos>
                        {/* <Datos>Origen: {character.origin?.name}</Datos> */}
                    </Detalles>
                <Detalles>
                    <Imagen src={character.image} alt={character.name} ></Imagen>
                    
                </Detalles>
                </div>) : <Datos>Loading...</Datos>
            }
        </div>
    )
}
