import styled from 'styled-components'
import img from '../resources/background.jpg'

const Container = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    min-height: 100vh;
    background-image: url(${img});
    background-size: cover;
`;

const ContCartas = styled.div`
    margin-top: 15px;
    display: flex;
    flex-direction: row;
    justify-content: center;
    gap: 50px;
    flex-wrap: wrap;
`
const Carta = styled.div`
    background-color: black;
    display: inline-block;
    border: 1px solid black;
    border-radius: 2%; 

    &:hover{
        box-shadow: 0 0 80px #FFFFFF;
        background-color: white;
    }
`
const Imagen = styled.img`
    border-radius: 4%;
`

const CloseBtn = styled.button`
    border-color: white;
    color: white;
    background-color: red;
    border-radius: 50%;
    &:hover{
        border-color: red;
        background-color: white;
        color: red;
    }
`;

const Data = styled.div`
    display: flex;
    justify-content: space-around;
    background-color: rgb(14, 82, 64);
    color: rgb(158, 230, 210);
    padding-left 15%;
    padding-right: 15%;
    border-radius: 6%;
    border: 1px solid white;
`
const ContBtn = styled.div`
    display: flex;
    justify-content: flex-end;    
    background-color: rgb(6, 52, 40);
    color: rgb(158, 230, 210);
    border-radius: 6%;
    border: 1px solid white;
    border-bottom: none;
`
const Datos = styled.h2`
    padding: 2px;
    background-color: rgb(255, 255, 255, 0.8); 
    color: rgb(14, 82, 64);
    box-shadow: 0px 0px 25px rgba(255, 255, 0, 0.5);
    border-radius: 4px;
`

const Barra = styled.input`
    border-radius: 5px;
    background-color: rgb(6, 52, 40);
    color: white;
    border: 2px solid white;
`

const Btn = styled.button`
    border-radius: 5px;
    background-color: rgb(6, 52, 40);
    color: white;
    border: 2px solid white;
`
const ContAbout = styled.div`
    display: inline-block;
`
const Navigate = styled.div`
    min-width: 100vh;
    color: rgb(14, 82, 64);
    padding: 25px;
    justify-content: space-around;
    display: flex;
    background-color: rgb(9, 169, 97);
    border-radius: 7px;
`
const Linked = styled.span`
    padding: 15px;
    background-color: black;    
    color: rgb(9, 169, 97);
    box-shadow: 0px 0px 25px rgba(255, 255, 0, 0.5);
    border-radius: 4px;
    border: 1px solid;
    border-color: white;
    &:hover{
        border-color: black;
        background-color: white;
        color: black;
    }
`

export {
    CloseBtn,
    Container,
    ContCartas,
    Carta,
    Imagen,
    Data,
    ContBtn,
    Datos,
    Barra,
    Btn,
    Navigate,
    Linked,
    ContAbout,
}
