import Card from './Card/Card';
import {ContCartas} from '../styles/styled-components'

export default function Cards(props) {
   const { characters, onClose } = props;

   if(characters.includes(characters.id)){
   return (
   <div>
      <ContCartas>
      {characters.length>0?characters.map((personaje) => (
      <Card
         name={personaje.name}
         species={personaje.species}
         gender={personaje.gender}
         image={personaje.image}
         onClose={()=>onClose(personaje.id)}
         key={personaje.id}
      />)):(<h3>No hay personajes.</h3>)}
   </ContCartas>
   </div>);
   } else {
      alert('El personaje ya existe.')
      return;
   }
}
