import { useSelector } from "react-redux";
import { Carta, Imagen, Data, ContBtn, Datos, Container, ContCartas} from '../../styles/styled-components';
import { Link } from "react-router-dom";

const Favorites = () => {

    const { myFavorites } = useSelector(state => state)

    return(
        <div style = {{maxWidth: "310px"}}>
            {
                myFavorites.map((character) =>{
                    return (
                        <Container>
                        <Carta>
                             <Data>
                                <ContBtn>
                                <Link to={`/detail/${character.id}`} style = {{textDecoration: "none"}}>
                                    <Datos>{character.name}</Datos>
                                </Link>
                                </ContBtn>
                            </Data>
                            
                            <Link to={`/detail/${character.id}`} style = {{textDecoration: "none"}}>
                                <Imagen src={character.image} alt={character.name} />

                                <Data>
                                    <Datos>{character.gender}</Datos>
                                    <Datos>{character.species}</Datos>
                                </Data>
                            </Link>
                            </Carta>
                            </Container>

                    )
                })
                      
            }
      </div>
    )
}

export default Favorites;
